import { useState,useContext, useEffect } from 'react';
import Layout from "../../components/ui/Layout";
import { UserContext } from "../../context/UserContext";
import { registerUser } from '../../api/UserApi';
import { useNavigate } from "react-router-dom";
import Form from 'react-bootstrap/Form';
import { getPreferencesList } from '../../api/PreferenceApi';

import './Register.css';

const newUser = {
    username: '',
    password: '',
    confirmPassword: '',
    fullName: '',
    email: '',
    zipCode: '',
    address: '',
    role: "User",
}

const Register = ({ children }) => {
    const navigate = useNavigate();    
    const [formFields, setFormFields] = useState(newUser);
    const { username, password, confirmPassword, fullName, email, zipCode, address, role } = formFields;
    const { setCurrentUser } = useContext(UserContext);

    const [preferences, setPreferences] = useState([]);
    const [restrictions, setRestrictions] = useState([]);
    const [numberOfResults, setNumberOfRecords] = useState(0);

    useEffect(() => {
      const fetchData = async () => {
          try {
              const data = await getPreferencesList(); // Assuming getPreferencesList is an async function that returns your data

              
              if (data.length > 0) {
                  let rArray = [];
                  let pArray = [];
                  for(var i =0; i < data.length; i++) {
                      if(data[i].typeId >= 0 && data[i].typeId < 4) {
                          rArray.push(data[i]); 
                      } else if (data[i].typeId > 4) {
                          pArray.push(data[i]);
                      }
                  }
                  setRestrictions(rArray);
                  setPreferences(pArray);
                  setNumberOfRecords(data.length);
              }
          } catch (error) {
              console.error("Error fetching preferences:", error);
          }
      }; 
      fetchData();
  }, []); // Run this effect only once when the component mounts

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormFields({...formFields,[name]:value});
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (password !== confirmPassword) {
            alert("password do not match");
            return;
        } try {
            const userRegister = { ...formFields, role: "User" }; 
            const registeredInUser = await registerUser(userRegister);
            
            if (registeredInUser) {                  
                registeredInUser.user.isAuthenticated = true;
                localStorage.setItem('user', JSON.stringify(registeredInUser.user));
                localStorage.setItem('token', registeredInUser.token);        
                setCurrentUser(registeredInUser.user);
                navigate("/Recomendation");
                }
            
        } catch(error) {
            console.log('user created encounter an error', error);
        }

    }

    
    return (
        <Layout>
            <h3>Register</h3>
            <Form onSubmit={handleSubmit} noValidate>
  <div className="row">
    <div  className="column">
      <label>Username</label>
      <div>
        <input type="text" onChange={handleChange} value={username} name="username"placeholder="Username" required />
        <div>
          Please choose a unique and valid username.
        </div>
      </div>
    <div>
    <label>Password</label>
    <div>
      <input type="password" onChange={handleChange} value={password} name="password" placeholder="Password" />
    </div>
  </div>
    <div>
    <label>Confirm Password</label>
    <div>
      <input type="password" onChange={handleChange} value={confirmPassword} name="confirmPassword" placeholder="confirmPassword" />
    </div>
  </div>
  <div>
      <label>Full name</label>
      <input type="text" onChange={handleChange} value={fullName} name="fullName" placeholder="Full name" required />
    </div>
    <div className="col-md-4 mb-3">
      <label>Email</label>
      <input type="text" onChange={handleChange} value={email} name="email" placeholder="Email" required />
    </div>
    <div >
      <label >Zip Code</label>
      <input type="number" onChange={handleChange} value={zipCode} name="zipCode" placeholder="Zip Code" required />
      <div>
        Please provide a valid zip code.
      </div>
    </div>
    <div className="col-md-3 mb-3">
      <label>Address</label>
      <input type="text" onChange={handleChange} value={address}  name="address" placeholder="State" required />
      <div className="invalid-tooltip">
        Please provide an address.
      </div>
    </div>


    <div>
      <label>Role</label>
      <div>
        <input type="text" onChange={handleChange} defaultValue={role} placeholder="User"  readOnly />
      </div>
    </div>
  </div>

  <button className="btn btn-primary" type="submit">Submit form</button>
</div>
</Form>

<div className="column">
<h2>My Preferences</h2>
                <Form /*onSubmit={handleSearch}*/>
                {(numberOfResults === 0) &&
                    <div>No preferences found</div>
                }
                    <p>Select your preferences: </p>
                    {preferences.map(preference => (
                    <Form.Check // prettier-ignore
                        key={preference.typeId} // Agrego un id unico
                        type="switch"
                        id={"custom-switch"} // Hago uso del unique id
                        label={preference.categoryName}
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name={preference.typeId}
                        // value={typeOfFood}
                        onChange={handleChange}
                    />
                    ))}
                    <p>Select your restrictions: </p>
                    {restrictions.map(restriction => (
                    <Form.Check // prettier-ignore
                        key={restriction.typeId} // Agrego un id unico
                        type="switch"
                        id={"custom-switch"} // Hago uso del unique id
                        label={restriction.categoryName}
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name={restriction.typeId}
                        // value={typeOfFood}
                        onChange={handleChange}
                    />
                    ))}
                  
                </Form>
</div>
{children}
        </Layout>
    )
}

export default Register;
