import React, { useState } from "react";
import Layout from "../../components/ui/Layout";
import Form from 'react-bootstrap/Form';
import { Button, ButtonGroup } from 'react-bootstrap';

const Preferences = () => {
    // aca guardo mis preferencias con el useState
    const[checkedPreference, setCheckedPreference] = useState([]);

    const handleChange = (e) => {
        // hago target al nombre de 
        const preferenceName = e.target.name;
        // hago target a los chequeados
        const isChecked = e.target.checked;
        const listOfPreference = [];
        if(isChecked) {
            console.log(preferenceName + " is checked");
            // hace update
            setCheckedPreference([...checkedPreference, preferenceName]);
        } else {
            console.log(preferenceName + " is unchecked");

        }
        console.log(listOfPreference);
    }
    

    return (
        <>
            <Layout>
                <h2>My Preferences</h2>
                <Form>
                    <p>Select your preferences: </p>
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Pizza"
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name="Pizza"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Burger"
                        name="Burger"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Pasta"
                        name="Pasta"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Mexican"
                        name="Mexican"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Asian"
                        name="Asian"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Peruvian"
                        name="Peruvian"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Grill"
                        name="Grill"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Seafood"
                        name="Seafood"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Indian"
                        name="Indian"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Healthy"
                        name="Healthy"
                        onChange={handleChange}
                    />
                    <br></br>
                    
                    <p>Select your restrictions: </p>
                    {/* VER SI AGREGAMOS ALGO */}
                    {/* <p>(This selection will limit your options)</p> */}
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Gluten Free"
                        name="Gluten Free"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Vegan"
                        name="Vegan"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Vegetarian"
                        name="Vegetarian"
                        onChange={handleChange}
                    />
                    
                    <br></br>

                    <p>Select the zip code of the preferred area: </p>
                    {/* VER SI AGREGAMOS ALGO */}
                    {/* <p>(This selection will limit your options)</p> */}
                    <Form.Select aria-label="Default select example">
                        <option>Open this select menu</option>
                        <option value="1">1414</option>
                        <option value="2">1425</option>
                        <option value="3">1409</option>
                    </Form.Select>
                    
                    
                    
                    <ButtonGroup>
                        <Button variant="primary" type="submit">
                            Submit
                        </Button>
                    </ButtonGroup>
                </Form>
            </Layout>
        </>
    )
}
export default Preferences;
