import React, { useEffect, useContext, useState } from "react";
import { UserContext } from "../../context/UserContext";
import Form from 'react-bootstrap/Form';
import { getUser } from "../../api/UserApi";

const UserProfile = () => {
    
    const userContext =  useContext(UserContext);
    const { setCurrentUser } = userContext.setCurrentUser;
    const user = userContext.user;

    const [userData, setUserData] = useState();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getUser(); // Assuming getPreferencesList is an async function that returns your data

                
                if (data.length > 0) {
                    let rArray = [];
                    let pArray = [];
                    for(var i =0; i < data.length; i++) {
                        if(data[i].typeId >= 0 && data[i].typeId < 4) {
                            rArray.push(data[i]); 
                        } else if (data[i].typeId > 4) {
                            pArray.push(data[i]);
                        }
                    }
                    setRestrictions(rArray);
                    setPreferences(pArray);
                    setNumberOfRecords(data.length);
                }
            } catch (error) {
                console.error("Error fetching preferences:", error);
            }
        }; 
        fetchData();
    }, []); // Run this effect only once when the component mounts

return (
    <>
    <h3>My Profile</h3>
      <Form.Group className="mb-3">
        <Form.Label>Username</Form.Label>
        <Form.Control placeholder="Disabled input" disabled />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Full Name</Form.Label>
        <Form.Control placeholder="Disabled input" disabled />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Email</Form.Label>
        <Form.Control placeholder="Disabled input" disabled />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Zip Code</Form.Label>
        <Form.Control placeholder="Disabled input" disabled />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Address</Form.Label>
        <Form.Control placeholder="Disabled input" disabled />
      </Form.Group>

    </>
)

}

export default UserProfile;
