import { useState,useContext } from 'react';
import { Button, ButtonGroup } from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import Layout from "../../components/ui/Layout";
import { UserContext } from "../../context/UserContext";
import LogoutButton from '../../components/form/LogoutButton';
import { useNavigate } from "react-router-dom";

const defaultFormFields = {
    username: '',
    password: '',
    confirmPassword: '',
    role: "user",
}

const Register = () => {
    const [formFields, setFormFields] = useState(defaultFormFields);
    const {username, password, confirmPassword, role} = formFields;

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormFields({...formFields,[name]:value});
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (password !== confirmPassword) {
            alert("password do not match");
            return;
        } try {
            // VER Y MODIFICAR EL METODO
            // const response = await createAuthUserWithEmailAndPassword(username, password);
            // console.log(response);
        } catch(error) {
            console.log('user created encounter an error', error);
        }

    }
    
    return (
        <Layout>
            <h3>Register Form</h3>
            <form className="needs-validation" onSubmit={handleSubmit} noValidate>
  <div className="form-row">
    <div className="col-md-4 mb-3">
      <label htmlFor="validationTooltipUsername">Username</label>
      <div className="input-group">
        <input type="text" onChange={handleChange} value={username} name="username" className="form-control" id="validationTooltipUsername" placeholder="Username" aria-describedby="validationTooltipUsernamePrepend" required />
        <div className="invalid-tooltip">
          Please choose a unique and valid username.
        </div>
      </div>
    </div>
    <div className="form-group row">
    <label>Password</label>
    <div className="col-sm-10">
      <input type="password" onChange={handleChange} value={password} name="password" placeholder="Password" />
    </div>
  </div>
    <div className="form-group row">
    <label>Confirm Password</label>
    <div className="col-sm-10">
      <input type="password" onChange={handleChange} value={confirmPassword} name="confirmPassword" placeholder="confirmPassword" />
    </div>
  </div>

  {/* AGREGAR CHECKBOX QUE NO PUEDA DESCHEQUEAR*/}
  <div className="col-md-4 mb-3">
      <label htmlFor="validationTooltipRole">Role</label>
      <div className="input-group">
        <input type="text" onChange={handleChange} defaultValue={role} className="form-control" id="validationTooltipRole" placeholder="User" aria-describedby="validationTooltipUsernamePrepend" readOnly />
      </div>
    </div>
  </div>
  <button className="btn btn-primary" type="submit">Submit form</button>
</form>
        </Layout>
    )
}

export default Register;
