import Layout from "../../components/ui/Layout";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

import './Recomendation.css';

const Recomendation = () => {

    const myRecommendation = [{
        name: 'Siga la vaca',
        description: 'Here goes the description',
        phoneNumber: '1154521569',
        rating: '4',
        zipCode: '1542',
        address: 'Beruti 453'
    }   
    ]   
    

    return (
        <>
        <Layout>
            <div className="container-center">
            <div>This is the recomendation for the day</div>
            {myRecommendation.map( recommendation => (
            <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src="holder.js/100px180" />
                <Card.Body>
                    <Card.Title>{recommendation.name}</Card.Title>
                    <Card.Text>
                            <ul>
                            <li>{recommendation.description}</li>
                            <li>{recommendation.phoneNumber}</li>
                            <li>{recommendation.rating}</li>
                            <li>{recommendation.zipCode}</li>
                            <li>{recommendation.address}</li>
                        </ul>
                        <a href="/RestaurantDetail">See more...</a>
                    </Card.Text>
                    <Button variant="primary"><i class="bi bi-heart-fill"></i></Button>
                    <Button variant="primary"><i class="bi bi-x-lg"></i></Button>
                </Card.Body>
            </Card>
                        )
                        )}
            </div>
        </Layout>
        </>
    )
}

export default Recomendation;
