import { useState, useEffect } from 'react';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Layout from '../../components/ui/Layout';
import { getUserLikes } from '../../api/RestaurantApi';
import Button from 'react-bootstrap/Button';
import { Link } from "react-router-dom";

import './Likes.css';

/////cambiar a matches???
const Likes = () => {
    const [matches, setMatches] = useState([]);
    const [numberOfResults, setNumberOfRecords] = useState(0);
    const [restaurantId, setRestaurantId] = useState(null);

    useEffect(() => {
        const fetchData = async() => {
            try {
                const restaurantMatches = await getUserLikes();
                
                setMatches(restaurantMatches);
                setRestaurantId(restaurantMatches.restaurantId);
                setNumberOfRecords(restaurantMatches.length);
            } catch(error) {
                console.log("Error fetching matches", error);
            }
        }
        fetchData();
    },[])


    const matchesList = [
        {
        restaurantName: 'Siga la Vaca',
        description: 'Argentinian steakhouse with a wide variety of meats and delicious sides.',
        phoneNumber: '1154521569',
        rating: 4.5,
        zipCode: '1542',
        address: 'Beruti 453, Buenos Aires, Argentina'
    },
    {
        restaurantName: 'Mama Mia Pizzeria',
        description: 'Authentic Italian pizza and pasta in a cozy, family-friendly atmosphere.',
        phoneNumber: '1155555555',
        rating: 4.3,
        zipCode: '1234',
        address: 'Via Roma 123, Rome, Italy'
    },
    {
        restaurantName: 'Sushi Paradise',
        description: 'Satisfy your sushi cravings with fresh, handcrafted sushi rolls and sashimi.',
        phoneNumber: '1123456789',
        rating: 4.7,
        zipCode: '5678',
        address: 'Sakura Street 789, Tokyo, Japan'
    },
    {
        restaurantName: 'La Brasserie Française',
        description: 'Experience the finest French cuisine with a wide selection of wines.',
        phoneNumber: '1198765432',
        rating: 4.6,
        zipCode: '9876',
        address: 'Rue de la Gourmandise 321, Paris, France'
    }

]

    return (
        <>
        <Layout>
            
            <div className='container'>
                <div className='matches-card'>
                <h3>My Likes</h3>
                {(numberOfResults === 0) &&
                    <div>No likes</div>
                }
                <Row xs={1} md={2} className="g-4">
                    {matchesList.map((match, idx) => (
                        <Col key={idx}>
                        <Card className='match-card'>
                            <Card.Img variant="top" src="holder.js/100px160" />
                            <Card.Body>
                            <Card.Title>{match.restaurantName}</Card.Title>
                            <Card.Text>
                            {match.description}
                            </Card.Text>
                            <Card.Text className="detail-info">
                                Phone Number: {match.phoneNumber}
                            </Card.Text>
                            <Card.Text className="detail-info">
                                Rating: {match.rating}
                            </Card.Text>
                            <Card.Text className="detail-info">
                                Zip Code: {match.zipCode}
                            </Card.Text>
                            <Card.Text className="detail-info">
                                Address: {match.address}
                            </Card.Text>
                            
                            <Link className="link-detail" to={`/Restaurant/Detail/${match.restaurantId}`}>
                                See more...
                            </Link>
                            <div className="btn-row">
                                <Button variant="primary" className="btn-dislike"><i className="bi bi-x-lg"></i></Button>
                            </div>
                            </Card.Body>
                        </Card>
                        </Col>
                    ))}
            </Row>
            </div>
        </div>
    </Layout>
    </>
    )
}

export default Likes;
