import { useState,useContext } from 'react';
import { Button, ButtonGroup } from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import Layout from "../../components/ui/Layout";
import { UserContext } from "../../context/UserContext";
import LogoutButton from '../../components/form/LogoutButton';
import { registerUser } from '../../api/UserApi';
import { useNavigate } from "react-router-dom";

const newUser = {
    username: '',
    password: '',
    confirmPassword: '',
    fullName: '',
    email: '',
    zipCode: '',
    address: '',
    role: "User",
}

const Register = () => {
    // const url = 'http://localhost:9000/post';
    const navigate = useNavigate();    
    const [formFields, setFormFields] = useState(newUser);
    const { username, password, confirmPassword, fullName, email, zipCode, address, role } = formFields;
    const { setCurrentUser } = useContext(UserContext);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormFields({...formFields,[name]:value});
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (password !== confirmPassword) {
            alert("password do not match");
            return;
        } try {
            const userRegister = { ...formFields, role: "User" }; 
            const registeredInUser = await registerUser(userRegister);
            
            if (registeredInUser) {                  
                registeredInUser.user.isAuthenticated = true;
                localStorage.setItem('user', JSON.stringify(registeredInUser.user));
                localStorage.setItem('token', registeredInUser.token);        
                setCurrentUser(registeredInUser.user);
                navigate("/");
                }
            
        } catch(error) {
            console.log('user created encounter an error', error);
        }

    }

    // const handleLogin = (event) => {
    //     if (event !== undefined) event.preventDefault();
    //     const loginCurrentUser = async () => {
    //         const userLogin = { 'username': username, 'password': password };
    //         const loggedInUser = await loginUser(userLogin);
    //         if (loggedInUser) {                  
    //             loggedInUser.user.isAuthenticated = true;
    //             localStorage.setItem('user', JSON.stringify(loggedInUser.user));
    //             localStorage.setItem('token', loggedInUser.token);        
    //             setCurrentUser(loggedInUser.user);
    //             navigate("/");
    //         }
    //     };
    //     loginCurrentUser();
    // };
    
    return (
        <Layout>
            <h3>Register Form</h3>
            <form onSubmit={handleSubmit} noValidate>
  <div>
    <div>
      <label>Username</label>
      <div>
        <input type="text" onChange={handleChange} value={username} name="username"placeholder="Username" required />
        <div>
          Please choose a unique and valid username.
        </div>
      </div>
    </div>
    <div>
    <label>Password</label>
    <div>
      <input type="password" onChange={handleChange} value={password} name="password" placeholder="Password" />
    </div>
  </div>
    <div>
    <label>Confirm Password</label>
    <div>
      <input type="password" onChange={handleChange} value={confirmPassword} name="confirmPassword" placeholder="confirmPassword" />
    </div>
  </div>
  <div>
      <label>Full name</label>
      <input type="text" onChange={handleChange} value={fullName} name="fullName" placeholder="Full name" required />
    </div>
    <div className="col-md-4 mb-3">
      <label>Email</label>
      <input type="text" onChange={handleChange} value={email} name="email" placeholder="Email" required />
    </div>
    <div >
      <label >Zip Code</label>
      <input type="number" onChange={handleChange} value={zipCode} name="zipCode" placeholder="Zip Code" required />
      <div>
        Please provide a valid zip code.
      </div>
    </div>
    <div className="col-md-3 mb-3">
      <label>Address</label>
      <input type="text" onChange={handleChange} value={address}  name="address" placeholder="State" required />
      <div className="invalid-tooltip">
        Please provide an address.
      </div>
    </div>

  {/* AGREGAR CHECKBOX QUE NO PUEDA DESCHEQUEAR*/}
  <div>
      <label>Role</label>
      <div>
        <input type="text" onChange={handleChange} defaultValue={role} placeholder="User"  readOnly />
      </div>
    </div>
  </div>
  <button className="btn btn-primary" type="submit">Submit form</button>
</form>
        </Layout>
    )
}

export default Register;
