import React, { useState, useContext, useEffect } from "react";
import Layout from "../../components/ui/Layout";
import Form from 'react-bootstrap/Form';
import { Button, ButtonGroup } from 'react-bootstrap';
import { preferencesUser } from "../../api/PreferenceApi";
import { UserContext } from "../../context/UserContext";
import { useNavigate } from "react-router-dom";
import { getPreferencesList } from "../../api/PreferenceApi";
//import { getUserPreferences} from "../../api/PreferenceApi";

import './Preferences.css';

const Preferences = () => {
    
    const userContext =  useContext(UserContext);
    const { setCurrentUser } = userContext.setCurrentUser;
    const user = userContext.currentUser;
    const navigate = useNavigate();    

    const [preferences, setPreferences] = useState([]);
    const [restrictions, setRestrictions] = useState([]);
    const [numberOfResults, setNumberOfRecords] = useState(0);
    // aca guardo mis preferencias con el useState
    const[checkedPreference, setCheckedPreference] = useState([]);
    const[checkedRestriction, setCheckedRestriction] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getPreferencesList();
                if (data.length > 0) {
                    let rArray = [];
                    let pArray = [];
                    for (var i = 0; i < data.length; i++) {
                        if (data[i].typeId >= 0 && data[i].typeId < 4) {
                            rArray.push(data[i]);
                        } else if (data[i].typeId > 3) {
                            pArray.push(data[i]);
                        }
                    }
                    setRestrictions(rArray);
                    setPreferences(pArray);
                    setNumberOfRecords(data.length);
                }
            } catch (error) {
                console.error("Error fetching preferences:", error);
            }
        };
        fetchData();
    }, []);

    const isPreferenceChecked = (preferenceTypeId) => {
        return checkedPreference.includes(preferenceTypeId);
    };

    const isRestrictionChecked = (restrictionTypeId) => {
        return checkedRestriction.includes(restrictionTypeId);
    };

    const handleChange = (e, isPreference) => {
        const preferenceTypeId = parseInt(e.target.name); 
        const isChecked = e.target.checked;

        if (isPreference) {
            if (isChecked) {
              setCheckedPreference((prevChecked) => [...prevChecked, preferenceTypeId]);
            } else {
              setCheckedPreference((prevChecked) =>
                prevChecked.filter((prevPref) => prevPref !== preferenceTypeId)
              );
            }
          } else {
            if (isChecked) {
              setCheckedRestriction((prevChecked) => [...prevChecked, preferenceTypeId]);
            } else {
              setCheckedRestriction((prevChecked) =>
                prevChecked.filter((prevRestr) => prevRestr !== preferenceTypeId)
              );
            }
          }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const userRegister = { ...checkedPreference };
            const preferenceUser = await preferencesUser(userRegister);

            if (preferenceUser) {
                preferenceUser.user.isAuthenticated = true;
                localStorage.setItem('typeId', JSON.stringify(preferenceUser.user));
                setCurrentUser(preferenceUser.user);
                navigate("/Recomendation");
            }
        } catch (error) {
            console.log('User preferences encountered an error', error);
        }
    };

  

    return (
        <>
            <Layout>
                <div className="container">
                <div className="form-container">
                <h2>My Preferences</h2>
                <Form /*onSubmit={handleSearch}*/>
                {(numberOfResults === 0) &&
                    <div>No preferences found</div>
                }
                    <h4>Select your preferences: </h4>
                    {preferences.map(preference => (
                    <Form.Check // prettier-ignore
                        key={preference.typeId} // Agrego un id unico
                        type="switch"
                        id={`custom-switch-${preference.typeId}`} // Hago uso del unique id
                        label={preference.categoryName}
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name={preference.typeId.toString()}
                        //value={typeOfFood}
                        onChange={(e) => handleChange(e, true)}
                        checked={isPreferenceChecked(preference.typeId)}
                    />
                    ))}
                    <h4>Select your restrictions: </h4>
                    {restrictions.map(restriction => (
                    <Form.Check // prettier-ignore
                        key={restriction.typeId} // Agrego un id unico
                        type="switch"
                        id={`custom-switch-${restriction.typeId}`} // Hago uso del unique id
                        label={restriction.categoryName}
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name={restriction.typeId}
                        // value={typeOfFood}
                        onChange={(e) => handleChange(e, false)}
                        checked={isRestrictionChecked(restriction.typeId)}
                    />
                    ))}


                    
                    {/* <div >
                    <label >Zip Code</label>
                    <input type="number" onChange={e => setZipCode(e.target.value)} value={zipCode} name="zipCode" placeholder="Zip Code" required />
                    <div>
                        Please provide a valid zip code.
                    </div>
                    </div> */}

                    <ButtonGroup>
                        <Button variant="primary" type="submit" onSubmit={handleSubmit}>
                            Submit
                        </Button>
                    </ButtonGroup>
                </Form>
                </div>
                </div>
            </Layout>
        </>
    )
}
export default Preferences;
