import { useState, useEffect } from "react";
import Layout from "../../components/ui/Layout";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import {useParams} from "react-router-dom";

import { getRestaurantById } from '../../api/RestaurantApi';

const Detail = () => {
    // const [name, setName] = useState();
    // const [description, setDescription] = useState();
    // const [phoneNumber, setPhoneNumber] = useState();
    // const [rating, setRating] = useState();
    // const [zipCode, setZipCode] = useState();
    // const [address, setAddress] = useState();
    let { restaurantId } = useParams();
    const[restaurantDetail, setRestaurantDetail] = useState({});
    const [callingDetail, setCalling] = useState(false);
    const [numberOfResults, setNumberOfRecords] = useState(0);
    useEffect(() => {
        const getDetail = () => {
        const restaurantDetail = async() => {
            if (!callingDetail) {
                setCalling(true);
                const localRestaurant = await getRestaurantById(restaurantId);
                setCalling(false);
                setRestaurantDetail(localRestaurant);
                
                console.log(localRestaurant);
                // setName(data.restaurantName);
                // setDescription(data.description);
                // setPhoneNumber(data.phoneNumber);
                // setRating(data.rating);
                // setZipCode(data.zipCode);
                // setAddress(data.address);
                setNumberOfRecords(restaurantDetail.length);
            } 
            restaurantDetail();
            }
        }
        getDetail();
    }, [])

    return (
        <Layout>
            <div className="container">
                <div>
            
            {(numberOfResults === 0) &&
                    <div>No restaurant found</div>
            }
            
                <Card style={{ width: '50rem'}}>
                <div className="recom-title">This are the details of the Restaurant</div>
                <Card.Img variant="top" src="" />
                    <Card.Body className="card-recom">
                        <Card.Title>{localRestaurant?.name}</Card.Title>
                        <Card.Text className="card-text">
                                <ul className="rest-info">
                                <li>{detail.description}</li>
                                <li>{detail.phoneNumber}</li>
                                <li>{detail.rating}</li>
                                <li>{detail.zipCode}</li>
                                <li>{detail.address}</li>
                            </ul>
                            <a className="link-detail" href="/Detail">See more...</a>
                        </Card.Text>
                        <div className="btn-row">
                            <Button variant="primary" className="btn-like"><i className="bi bi-heart-fill"></i></Button>
                            <Button variant="primary" className="btn-dislike"><i className="bi bi-x-lg"></i></Button>
                        </div>
                    </Card.Body>
                </Card>
                </div>
            </div>
        </Layout>
    )
}

export default Detail;
