import { useEffect, useState } from "react";
import Layout from "../../components/ui/Layout";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

import './Recomendation.css';

import { getRandomRestaurant } from "../../api/RestaurantApi";

const Recomendation = () => {
    // const [randomRestaurant, setRandomRestaurant] = useState([]);
    const [name, setName] = useState();
    const [description, setDescription] = useState();
    const [photo, setPhoto] = useState();
    const [numberOfResults, setNumberOfRecords] = useState(0);


    useEffect(() => {
        const fetchData = async() => {
            try{
                const data = await getRandomRestaurant();
                console.log(data);


                    setName(data.restaurantName);
                    setDescription(data.description);
                    setPhoto(data.photoPath);
                    setNumberOfRecords(data.length);


            } catch (error) {
                console.error("Error fetching preferences:", error);
            }
        }
        fetchData();
    },[])

    const myRecommendation = [{
        name: 'Siga la vaca',
        description: 'Here goes the description',
        phoneNumber: '1154521569',
        rating: '4',
        zipCode: '1542',
        address: 'Beruti 453'
    }   
    ]   
    

    return (
        <>
        <Layout>
            <div className="container">
                <div>
            <div className="recom-title">This is the recomendation for the day</div>
            {(numberOfResults === 0) &&
                    <div>No restaurant found</div>
            }
            
                <Card style={{ width: '30rem' }}>
                <Card.Img variant="top" src={photo} />
                    <Card.Body>
                        <Card.Title>{name}</Card.Title>
                        <Card.Text>
                                <ul className="rest-info">
                                <li>{description}</li>
                                {/* <li>{recommendation.phoneNumber}</li>
                                <li>{recommendation.rating}</li>
                                <li>{recommendation.zipCode}</li>
                                <li>{recommendation.address}</li> */}
                            </ul>
                            <a className="link-detail" href="/RestaurantDetail">See more...</a>
                        </Card.Text>
                        <div className="btn-row">
                            <Button variant="primary" className="btn-like"><i className="bi bi-heart-fill"></i></Button>
                            <Button variant="primary" className="btn-dislike"><i className="bi bi-x-lg"></i></Button>
                        </div>
                    </Card.Body>
                </Card>
                </div>
            </div>
        </Layout>
        </>
    )
}

export default Recomendation;
