import { useState,useContext, useEffect } from 'react';
import Layout from "../../components/ui/Layout";
import { UserContext } from "../../context/UserContext";
import { registerUser } from '../../api/UserApi';
import { useNavigate } from "react-router-dom";
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import { getPreferencesList } from '../../api/PreferenceApi';

import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import './Register.css';

const newUser = {
    username: '',
    password: '',
    confirmPassword: '',
    fullName: '',
    email: '',
    zipCode: '',
    address: '',
    role: "User",
    preferencesArray : []
}

const Register = ({ children }) => {
    const navigate = useNavigate();    
    const [formFields, setFormFields] = useState(newUser);
    const { username, password, confirmPassword, fullName, email, zipCode, address, role, preferencesArray } = formFields;
    const { setCurrentUser } = useContext(UserContext);

    const [selectedPreferences, setSelectedPreferences] = useState([]);
    const [restrictions, setRestrictions] = useState([]);
    const [numberOfResults, setNumberOfRecords] = useState(0);

    const[checkedPreference, setCheckedPreference] = useState([]);

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    useEffect(() => {
      const fetchData = async () => {
          try {
              const data = await getPreferencesList(); // Assuming getPreferencesList is an async function that returns your data

              
              if (data.length > 0) {
                  let rArray = [];
                  let pArray = [];
                  for(var i =0; i < data.length; i++) {
                      if(data[i].typeId >= 0 && data[i].typeId < 4) {
                          rArray.push(data[i]); 
                      } else if (data[i].typeId > 4) {
                          pArray.push(data[i]);
                      }
                  }
                  setRestrictions(rArray);
                  setSelectedPreferences(pArray);
                  setNumberOfRecords(data.length);
              }
          } catch (error) {
              console.error("Error fetching preferences:", error);
          }
      }; 
      fetchData();
  }, []); // Run this effect only once when the component mounts



    const handleSubmit = async (event) => {
        event.preventDefault();
        if (password !== confirmPassword) {
            alert("password do not match");
            return;
        } try {
            const userRegister = { ...formFields, role: "User" }; 
            const registeredInUser = await registerUser(userRegister);
            
            if (registeredInUser) {                  
                registeredInUser.user.isAuthenticated = true;
                localStorage.setItem('user', JSON.stringify(registeredInUser.user));
                localStorage.setItem('token', registeredInUser.token);        
                setCurrentUser(registeredInUser.user);
                navigate("/Recomendation");
                }
            
        } catch(error) {
            console.log('user created encounter an error', error);
        }

    }

    const handleChange = (event) => {
      const { name, value, checked } = event.target;
      setFormFields({...formFields,[name]:value});

      if(checked) {
        setSelectedPreferences([...selectedPreferences, name]);
      } else {
        setSelectedPreferences([selectedPreferences.filter(prevPref => prevPref !== name)]);
        console.log("unchecked");
      }
  }
  
    const handleSave = (e) => {
      if(selectedPreferences.length === 0) {
        alert("Please choose at least one preference");
      } else {
        selectedPreferences.push(checkedPreference);
        console.log(selectedPreferences);
         // If you want to set the selected preferences back to your formFields state, you can do it here:
    setFormFields({ ...formFields, preferencesArray: selectedPreferences });

    // Close the modal or perform other actions as needed.
    handleClose();
      }
    }

    
    return (
        <Layout>
            <h3>Register</h3>
            <Form onSubmit={handleSubmit} noValidate>
            <InputGroup className="mb-3">
              <InputGroup.Text id="inputGroup-sizing-default">
              Username
                </InputGroup.Text>
              <Form.Control
                aria-label="Default"
                aria-describedby="inputGroup-sizing-default"
                type="text" 
                onChange={handleChange} 
                value={username} 
                name="username"
                placeholder="Username" 
                required
              />
            </InputGroup>

            <InputGroup className="mb-3">
              <InputGroup.Text id="inputGroup-sizing-default">
              Password
                </InputGroup.Text>
              <Form.Control
                aria-label="Default"
                aria-describedby="inputGroup-sizing-default"
                type="password" 
                onChange={handleChange} 
                value={password} 
                name="password" 
                placeholder="Password"
              />
            </InputGroup>

            <InputGroup className="mb-3">
              <InputGroup.Text id="inputGroup-sizing-default">
              Confirm Password
                </InputGroup.Text>
              <Form.Control
                aria-label="Default"
                aria-describedby="inputGroup-sizing-default"
                type="password" 
                onChange={handleChange} 
                value={confirmPassword} 
                name="confirmPassword" 
                placeholder="Confirm password"
              />
            </InputGroup>

            <InputGroup className="mb-3">
              <InputGroup.Text id="inputGroup-sizing-default">
              Full name
                </InputGroup.Text>
              <Form.Control
                aria-label="Default"
                aria-describedby="inputGroup-sizing-default"
                type="text" 
                onChange={handleChange} 
                value={fullName} 
                name="fullName" 
                placeholder="Full name" 
                required
              />
            </InputGroup>

            <InputGroup className="mb-3">
              <InputGroup.Text id="inputGroup-sizing-default">
              Email
                </InputGroup.Text>
              <Form.Control
                aria-label="Default"
                aria-describedby="inputGroup-sizing-default"
                type="text" 
                onChange={handleChange} 
                value={email} 
                name="email" 
                placeholder="Email" 
                required
              />
            </InputGroup>

            <InputGroup className="mb-3">
              <InputGroup.Text id="inputGroup-sizing-default">
              Zip Code
                </InputGroup.Text>
              <Form.Control
                aria-label="Default"
                aria-describedby="inputGroup-sizing-default"
                type="number" 
                onChange={handleChange} 
                value={zipCode} 
                name="zipCode" 
                placeholder="Zip Code" 
                required
              />
            </InputGroup>

            <InputGroup className="mb-3">
              <InputGroup.Text id="inputGroup-sizing-default">
              Address
                </InputGroup.Text>
              <Form.Control
                type="text" 
                onChange={handleChange} 
                value={address}  
                name="address" 
                placeholder="Address" 
                required
              />
            </InputGroup>

            <InputGroup className="mb-3">
              <InputGroup.Text id="inputGroup-sizing-default">
              Role
                </InputGroup.Text>
            <Form.Control
        type="text"
        placeholder="User"
        aria-label="Disabled input example"
        readOnly
      />
                  </InputGroup>

                  <Button variant="warning" onClick={handleShow}>
        Choose your preferences
      </Button>

      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>My Preferences</Modal.Title>
        </Modal.Header>
        <Modal.Body>
                <Form /*onSubmit={handleSearch}*/>
                {(numberOfResults === 0) &&
                    <div>No preferences found</div>
                }
                {/*CAMBIAR PREFERENCE POR TIPO DE COMIDA U OTRO*/}
                    <p>Select your preferences: </p>
                    {selectedPreferences.map(preference => (
                    <Form.Check // prettier-ignore
                        key={`preference-${preference.typeId}`} // Agrego un id unico
                        type="switch"
                        id={"custom-switch"} // Hago uso del unique id
                        label={preference.categoryName}
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name={preference.typeId}
                        value={preferencesArray}
                        onChange={handleChange}
                    />
                    ))}
                    <p>Select your restrictions: </p>
                    {restrictions.map(restriction => (
                    <Form.Check // prettier-ignore
                        key={restriction.typeId} // Agrego un id unico
                        type="switch"
                        id={"custom-switch"} // Hago uso del unique id
                        label={restriction.categoryName}
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name={restriction.typeId}
                        value={preferencesArray}
                        onChange={handleChange}
                    />
                    ))}
                  
                </Form></Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSave}>
            Save My Preferences
          </Button>
        </Modal.Footer>
      </Modal>


  <button className="btn btn-danger" type="submit">Submit form</button>
</Form>



          {children}
        </Layout>
    )
}

export default Register;
