import { useState, useEffect } from "react";
import Layout from "../../components/ui/Layout";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import {useParams} from "react-router-dom";
import Loading from '../../components/ui/Loading';

import { getRestaurantById } from '../../api/RestaurantApi';

import './Recomendation.css';

const Detail = () => {
    // const [name, setName] = useState();
    // const [description, setDescription] = useState();
    // const [phoneNumber, setPhoneNumber] = useState();
    // const [rating, setRating] = useState();
    // const [zipCode, setZipCode] = useState();
    // const [address, setAddress] = useState();
    let { restaurantId } = useParams();
    const[restaurantDetail, setRestaurantDetail] = useState({});
    const [callingDetail, setCalling] = useState(false);
    const [numberOfResults, setNumberOfRecords] = useState(0);
    
    
    useEffect(() => {
        const getDetail = () => {
        const restaurantDetail = async() => {
            if (!callingDetail) {
                setCalling(true);
                const localRestaurant = await getRestaurantById(restaurantId);
                setCalling(false);
                setRestaurantDetail(localRestaurant);
                
                console.log(localRestaurant);
                setNumberOfRecords(restaurantDetail.length);
            } 
            }
            restaurantDetail();
        }
        getDetail();
    }, [])

    if (callingDetail) return <Loading />;

    return (
        <Layout>
            <div className="container">
                <div>
            
            {(numberOfResults === 0) &&
                    <div>No restaurant found</div>
            }
            
                <Card style={{ width: '50rem'}}>
                <div className="recom-title">This are the details of the Restaurant</div>
                <Card.Img variant="top" src={restaurantDetail?.photoPath} />
                    <Card.Body className="card-recom">
                        <Card.Title>{restaurantDetail?.name}</Card.Title>
                        <Card.Text className="card-text">
                            {restaurantDetail?.description}
                        </Card.Text>
                        <Card.Text>
                            Phone Number: {restaurantDetail?.phoneNumber}
                        </Card.Text>
                        <Card.Text>
                            Rating: {restaurantDetail?.rating}
                        </Card.Text>
                        <Card.Text>
                            Zip Code: {restaurantDetail?.zipCode}
                        </Card.Text>
                        <Card.Text>
                            Address: {restaurantDetail?.address}
                        </Card.Text>
                        <Card.Link className="link-detail" href="/Detail">See more...</Card.Link>

                        <div className="btn-row">
                            <Button variant="primary" className="btn-like"><i className="bi bi-heart-fill"></i></Button>
                            <Button variant="primary" className="btn-dislike"><i className="bi bi-x-lg"></i></Button>
                        </div>
                    </Card.Body>
                </Card>
                </div>
            </div>
        </Layout>
    )
}

export default Detail;
