import React, { useState, useContext, useEffect } from "react";
import Layout from "../../components/ui/Layout";
import Form from 'react-bootstrap/Form';
import { Button, ButtonGroup } from 'react-bootstrap';
import { preferencesUser } from "../../api/PreferenceApi";
import { UserContext } from "../../context/UserContext";
import { useNavigate } from "react-router-dom";
import { getPreferencesList } from "../../api/PreferenceApi";
//import { getUserPreferences} from "../../api/PreferenceApi";

import './Preferences.css';

const Preferences = () => {
    
    const userContext = useContext(UserContext);
    const { setCurrentUser } = userContext;
    const user = userContext.currentUser;
    const navigate = useNavigate();
  
    const [preferences, setPreferences] = useState([]);
    const [restrictions, setRestrictions] = useState([]);
    const [numberOfResults, setNumberOfResults] = useState(0);
    const [checkedPreference, setCheckedPreference] = useState([]);
    const [checkedRestriction, setCheckedRestriction] = useState([]);
  
    const isItemChecked = (itemId, isPreference) => {
      const items = isPreference ? checkedPreference : checkedRestriction;
      return items.includes(itemId);
    };
  
    const toggleItem = (itemId, isPreference) => {
      const items = isPreference ? checkedPreference : checkedRestriction;
      const updatedItems = items.includes(itemId)
        ? items.filter(item => item !== itemId)
        : [...items, itemId];
  
      if (isPreference) {
        setCheckedPreference(updatedItems);
      } else {
        setCheckedRestriction(updatedItems);
      }
    };
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          const data = await getPreferencesList();
  
          if (data.length > 0) {
            const rArray = data.filter(item => item.typeId >= 0 && item.typeId < 4);
            const pArray = data.filter(item => item.typeId > 3);
  
            setRestrictions(rArray);
            setPreferences(pArray);
            setNumberOfResults(data.length);
          }
        } catch (error) {
          console.error("Error fetching preferences:", error);
        }
      };
  
      fetchData();
    }, []);
  
    const handleSubmit = async (event) => {
      event.preventDefault();
  
      try {
        const userRegister = { ...checkedPreference, ...checkedRestriction };
        const preferenceUser = await preferencesUser(userRegister);
  
        if (preferenceUser) {
          preferenceUser.user.isAuthenticated = true;
          localStorage.setItem('typeId', JSON.stringify(preferenceUser.user));
  
          setCurrentUser(preferenceUser.user);
          navigate("/Recomendation");
        }
  
        console.log("se guardo el array", checkedPreference);
      } catch (error) {
        console.log('user created encounter an error', error);
      }
    };
  
    return (
      <>
        <Layout>
          <div className="container">
            <div className="form-container">
              <h2>My Preferences</h2>
              <Form>
                {(numberOfResults === 0) && <div>No preferences found</div>}
                <h4>Select your preferences: </h4>
                {preferences.map(preference => (
                  <Form.Check
                    key={preference.typeId}
                    type="switch"
                    id={`custom-switch-${preference.typeId}`}
                    label={preference.categoryName}
                    name={preference.typeId}
                    onChange={() => toggleItem(preference.typeId, true)}
                    checked={isItemChecked(preference.typeId, true)}
                  />
                ))}
                <h4>Select your restrictions: </h4>
                {restrictions.map(restriction => (
                  <Form.Check
                    key={restriction.typeId}
                    type="switch"
                    id={`custom-switch-${restriction.typeId}`}
                    label={restriction.categoryName}
                    name={restriction.typeId}
                    onChange={() => toggleItem(restriction.typeId, false)}
                    checked={isItemChecked(restriction.typeId, false)}
                  />
                ))}
                <ButtonGroup>
                  <Button variant="primary" type="submit" onClick={handleSubmit}>
                    Submit
                  </Button>
                </ButtonGroup>
              </Form>
            </div>
          </div>
        </Layout>
      </>
    );
  };

export default Preferences;
