import React, { useState } from "react";
import Layout from "../../components/ui/Layout";
import Form from 'react-bootstrap/Form';
import { Button, ButtonGroup } from 'react-bootstrap';

const Preferences = () => {
    // aca guardo mis preferencias con el useState
    const[checkedPreference, setCheckedPreference] = useState([]);

    const handleChange = (e) => {
        // guardo el nombre de mi preferencia en la variable
        const preferenceName = e.target.name;
        // guardo los chequeados
        const isChecked = e.target.checked;

        if(isChecked) {
            console.log(preferenceName + " is checked");
            // hace update de mi lista y agrega el que chequee recientemente
            setCheckedPreference([...checkedPreference, preferenceName]);
        } else {
            console.log(preferenceName + " is unchecked");
            //lo que hace filter es que me crea un nuevo array
            //con los elementos que cumplan con la condicion que escribi
            //cada vez que sea true conserva mi item, sino lo ignora y no lo agrega al array
            setCheckedPreference(checkedPreference.filter(preference => preference !== preferenceName));
        }
        console.log("This is the list of my preferences: " + checkedPreference);
    }

    const handleSubmit = async(event) => {
        event.preventDefault();
        try {
            const userRegister = checkedPreference; 
            const preferenceUser = await preferenceUser(userRegister);
            
            if (preferenceUser) {                  
                preferenceUser.user.isAuthenticated = true;
                localStorage.setItem('user', JSON.stringify(registeredInUser.user));
                 
                setCurrentUser(registeredInUser.user);
                navigate("/Recomendation");
                }
            

        } catch(error) {
            console.log('user created encounter an error', error);
        }
    }
    

    return (
        <>
            <Layout>
                <h2>My Preferences</h2>
                <Form>
                    <p>Select your preferences: </p>
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Pizza"
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name="4"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Burger"
                        name="5"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Pasta"
                        name="6"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Mexican"
                        name="7"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Asian"
                        name="8"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Peruvian"
                        name="9"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Grill"
                        name="10"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Seafood"
                        name="11"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Indian"
                        name="12"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Healthy"
                        name="13"
                        onChange={handleChange}
                    />
                    <br></br>
                    
                    <p>Select your restrictions: </p>
                    {/* VER SI AGREGAMOS ALGO */}
                    {/* <p>(This selection will limit your options)</p> */}
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Gluten Free"
                        name="3"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Vegan"
                        name="2"
                        onChange={handleChange}
                    />
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label="Vegetarian"
                        name="1"
                        onChange={handleChange}
                    />
                    
                    <br></br>

                    <p>Select the zip code of the preferred area: </p>
                    {/* VER SI AGREGAMOS ALGO */}
                    {/* <p>(This selection will limit your options)</p> */}
                    <Form.Select aria-label="Default select example">
                        <option>Open this select menu</option>
                        <option value="1">1414</option>
                        <option value="2">1425</option>
                        <option value="3">1409</option>
                    </Form.Select>
                    
                    
                    
                    <ButtonGroup>
                        <Button variant="primary" type="submit" onSubmit={handleSubmit}>
                            Submit
                        </Button>
                    </ButtonGroup>
                </Form>
            </Layout>
        </>
    )
}
export default Preferences;
