import React, { useState, useContext } from "react";
import Layout from "../../components/ui/Layout";
import Form from 'react-bootstrap/Form';
import { Button, ButtonGroup } from 'react-bootstrap';
import { preferencesUser } from "../../api/PreferenceApi";
import { UserContext } from "../../context/UserContext";
import { useNavigate } from "react-router-dom";
import { getPreferencesList } from "../../api/PreferenceApi";


const Preferences = () => {
    
    const userContext =  useContext(UserContext);
    const { setCurrentUser } = userContext.setCurrentUser;
    const user = userContext.user;
    const [preferences, setPreferences] = useState([]);
    const [numberOfResults, setNumberOfRecords] = useState(0);


    const perferencesArray = [
        {id: 1, categoriesName: "Veggie"},
        {id: 2, categoriesName: "Vegan"},
        {id: 3, categoriesName: "Gluten-free"}
    ];

    // aca guardo mis preferencias con el useState
    const[checkedPreference, setCheckedPreference] = useState([]);
    const [zipCode, setZipCode] =  useState();

    const navigate = useNavigate();    

    const handleChange = (e) => {
        // guardo el nombre de mi preferencia en la variable
        const preferenceName = e.target.name;
        // guardo los chequeados
        const isChecked = e.target.checked;

        if(isChecked) {
            console.log(preferenceName + " is checked");
            // hace update de mi lista y agrega el que chequee recientemente
            setCheckedPreference([...checkedPreference, preferenceName]);
        } else {
            console.log(preferenceName + " is unchecked");
            //lo que hace filter es que me crea un nuevo array
            //con los elementos que cumplan con la condicion que escribi
            //cada vez que sea true conserva mi item, sino lo ignora y no lo agrega al array
            setCheckedPreference(checkedPreference.filter(preference => preference !== preferenceName));
        }
        console.log("This is the list of my preferences: " + checkedPreference);
    }

    const handleSubmit = async(event) => {
        event.preventDefault();
        try {
            const userRegister = { ...checkedPreference, zipCode }; 
            const preferenceUser = await preferencesUser(userRegister);
            
            
            if (preferenceUser) {                  
                preferenceUser.user.isAuthenticated = true;
                localStorage.setItem('typeId', JSON.stringify(preferenceUser.user));
                 
                setCurrentUser(preferenceUser.user);
                navigate("/Recomendation");
                }
            
                console.log("se guardo el array" + checkedPreference);
        } catch(error) {
            console.log('user created encounter an error', error);
        }
    }

    const handleSearch = (event) => {
        if (event !== undefined) event.preventDefault();
        const search = async () => {
            getPreferencesList().then(data => {
            console.log("Found:" + data.length + " records");
            setPreferences(data);
            setNumberOfRecords(data.length);
          }).catch(error=>{        
            alert("did it work??");
            throw new Error("Could not get movies from server");
            
          });
        }
        search();
      };
    

    return (
        <>
            <Layout>
                <h2>My Preferences</h2>
                <Form onSubmit={handleSearch}>
                {(numberOfResults === 0) &&
            <div>No movies found</div>
          }
                    <p>Select your preferences: </p>
                    {preferences.map(preference => (
                    <Form.Check // prettier-ignore
                        type="switch"
                        id="custom-switch"
                        label={preference.categoriesName}
                        // le voy asignando el nombre para ir guardandolo en la base de datos
                        name={preference.id}
                        // value={typeOfFood}
                        onChange={handleChange}
                    />
                    ))}


                    <p>Select the zip code of the preferred area: </p>


                    
                    <div >
                    <label >Zip Code</label>
                    <input type="number" onChange={e => setZipCode(e.target.value)} value={zipCode} name="zipCode" placeholder="Zip Code" required />
                    <div>
                        Please provide a valid zip code.
                    </div>
                    </div>

                    <ButtonGroup>
                        <Button variant="primary" type="submit" onSubmit={handleSubmit}>
                            Submit
                        </Button>
                    </ButtonGroup>
                </Form>
            </Layout>
        </>
    )
}
export default Preferences;
